@import "variables";

* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;
}

html {
  background-color: white;
  overflow: hidden;
  cursor: default;
}

.wrapper {
  position: relative;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  justify-content: space-evenly;
  align-content: center;
  height: 100vh;
  width: 70%;
  font-family: 'Rubik', sans-serif;

  .card {
    position: absolute;
    left: 15%;
    top: 50%;
    transform: translateY(-50%);
    display: flex;
    flex-direction: column;
    height: 600px;
    width: 300px;
    border-radius: 20px;
    border: solid 15px white;
    background-color: white;
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.75);
    -moz-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.75);
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.75);

    .card__contact {
      .contact__info {
        position: relative;
        width: 100%;
        height: 70px;
        display: flex;
        flex-direction: row;
        padding-top: 20px;
        padding-left: 30px;
        align-items: center;
        border-radius: 20px 20px 10px 10px;
        background: linear-gradient(90deg, rgba(136, 56, 255, 1) 0%, rgba(233, 66, 255, 1) 100%);
        z-index: 10000;
        overflow: hidden;


        .contact__name {
          display: flex;
          flex-direction: column;
          margin-left: 10px;
          color: white;
          font-size: 14px;

          .info__name {
            font-weight: 900;
            opacity: 80%;
          }

          .info__status {
            opacity: 40%;
          }
        }

        .contact__photo {
          position: relative;
          width: 32px;
          height: 32px;
          border-radius: 50%;
          border: solid 2px white;
          cursor: pointer;
        }

        &:before {
          position: absolute;
          top: 0;
          left: 50%;
          transform: translateX(-50%);
          content: '';
          display: inline-block;
          width: 50%;
          height: 20px;
          background-color: white;
          border-bottom-left-radius: 25px;
          border-bottom-right-radius: 25px;
        }

        .card__settings {
          position: absolute;
          top: 65%;
          transform: translateY(-50%);
          right: 5%;
          z-index: 10000;
          width: 3px;
          height: 3px;
          border-radius: 50%;
          background-color: white;
          cursor: pointer;

          &:before {
            position: absolute;
            content: '';
            display: inline-block;
            height: 3px;
            width: 3px;
            border-radius: 50%;
            background-color: white;
            top: 5px;
          }

          &:after {
            position: absolute;
            content: '';
            display: inline-block;
            height: 3px;
            width: 3px;
            border-radius: 50%;
            background-color: white;
            bottom: 5px;
          }
        }
      }


      .card__content {
        position: relative;
        top: -3%;
        padding-top: 20px;
        z-index: 1;
        height: 515px;
        border-radius: 0px 20px 20px;
        background-color: hsl(270, 20%, 96%);
        overflow: hidden;

        .content__list {
          .content__item {
            -webkit-box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.75);
            -moz-box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.75);
            box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.75);
            font-size: 12px;
          }

          .left {
            color: hsl(276, 55%, 52%);
            display: flex;
            background-color: rgba(146, 65, 200, 30%);
            width: 60%;
            border-radius: 10px;
            padding: 5px 10px;
            margin: 8px 6px;
          }

          .left__special {
            position: relative;
            background: linear-gradient(90deg, rgba(136, 56, 255, 1) 0%, rgba(233, 66, 255, 1) 100%);
            color: white;
            height: 40px;
            width: 70%;
            line-height: 40px;
            padding: 0 40px;
            font-weight: 100;

            &:before {
              position: absolute;
              top: 50%;
              transform: translateY(-50%);
              left: 10px;
              content: '';
              display: inline-block;
              width: 16px;
              height: 16px;
              border: solid 1px white;
              border-radius: 50%;
            }

            b {
              position: absolute;
              right: 10px;
              font-size: 16px;
            }
          }

          .right {
            display: flex;
            width: 60%;
            justify-content: flex-end;
            text-align: right;
            background-color: white;
            color: hsl(271, 15%, 43%);
            margin: 6px 6px 6px 100px;
            border-radius: 10px;
            padding: 6px;

            .item__photo {
              width: 42px;
              height: 42px;
              border-radius: 10px;
              margin-right: 5px;
              background-color: transparent;
            }
          }
        }

        .message {
          display: flex;
          position: relative;
          margin: 30px auto 0;
          background-color: white;
          width: 90%;
          height: 40px;
          line-height: 40px;
          color: grey;
          font-size: 10px;
          padding-left: 20px;
          border-radius: 30px;
          letter-spacing: 1px;

          .message__type {
            opacity: 40%;
          }

          .message__button {
            position: absolute;
            top: 50%;
            transform: translateY(-50%);
            right: 5px;
            height: 36px;
            width: 36px;
            background-color: hsl(271, 36%, 24%);
            border-radius: 50%;
            cursor: pointer;

            &:after {
              position: absolute;
              top: 42%;
              left: 50%;
              transform: translate(-50%, -50%) rotate(45deg);
              content: '';
              display: inline-block;
              height: 2px;
              width: 10px;
              background-color: white;
            }

            &:before {
              position: absolute;
              top: 58%;
              left: 50%;
              transform: translate(-50%, -50%) rotate(135deg);
              content: '';
              display: inline-block;
              height: 2px;
              width: 10px;
              background-color: white;
            }
          }
        }
      }
    }
  }

  .info {
    position: absolute;
    right: 6%;
    top: 50%;
    width: 40%;
    transform: translateY(-50%);
    display: flex;
    flex-direction: column;
    justify-content: center;

    .info__header {
      margin-bottom: 40px;
      font-weight: 500;
      color: hsl(271, 36%, 24%);
    }

    .info__content {
      color: hsl(270, 7%, 64%);
    }
  }
}

.pattern__top {
  position: absolute;
  left: -8%;
  background: linear-gradient(180deg, rgba(136, 56, 255, 1) 0%, rgba(233, 66, 255, 1) 100%);
  width: 600px;
  height: 700px;
  border-radius: 0 0 50% 50%;
}

.pattern__bottom {
  position: absolute;
  right: -9%;
  bottom: 0;
  background-color: #F8F4FA;
  width: 600px;
  height: 700px;
  border-radius: 50% 50% 0 0;
}
